	background-size: 40px 40px;

	-webkit-animation: progress2 2s infinite linear;
	animation: progress2 2s infinite linear;
}

@-webkit-keyframes progress2 {
	0%{background-position:0 0}
	100%{background-position:40px 0}
}

@keyframes progress2 {
	0%{background-position:0 0}
	100%{background-position:40px 0}
}
