  return !!window.opera || /opera|opr/i.test(navigator.userAgent);
});
</pre>

### IE 10

<pre>
conditionizr.add('ie10', [], function () {
  var version = false;
  /*@cc_on
      if (/^10/.test(@_jscript_version) &amp;&amp; /MSIE 10\.0(?!.*IEMobile)/i.test(navigator.userAgent))
      version = true
  @*/
